import React from "react";
import { createUseStyles } from 'react-jss'
import { CSSTransition } from 'react-transition-group';

const useStyles = createUseStyles({
  container: {
    display: "block",
    width: "100%",
    marginTop: "30px"
  },
  slideUp: {
    position: "absolute",
    top: "0",
    left: "0",
    backgroundColor: "#000",
    overflow: 'auto',
    height: "100vh",
    width: "100vw",
    transformOrigin: "top",
    transition: "transform .5s ease",
    transform: "scaleY(0)",
  },
  enterActive: {
    transform: "scaleY(1)",
  },
  enterDone: {
    transform: "scaleY(1)",
  },
  exitActive: {
    transform: "scaleY(0)",
  },
})

export default function SliderComponent({ page, children }) {
  const classes = useStyles()

  return (
    <CSSTransition in={page !== null} timeout={300} classNames={{
        enterActive: classes.enterActive,
        enterDone: classes.enterDone,
        exitActive: classes.exitActive,
      }}>
        <div className={classes.slideUp}>
          {children}
        </div>
    </CSSTransition>
  )
}
