import React, { useState } from "react";
import { createUseStyles } from 'react-jss'
import { CSSTransition } from 'react-transition-group';

const useStyles = createUseStyles({
  container: {
    display: "block",
    width: "100%",
    marginTop: "30px"
  },
  exit: {
    margin: "auto auto",
    height: "min-content",
    width: "min-content",
    textAlign:"center",
    fontSize: "50px",
    transform: "rotate(45deg)",
    fontWeight: "200"
  },
  slideUp: {
    position: "absolute",
    top: "0",
    left: "0",
    backgroundColor: "#000",
    overflow: 'auto',
    height: "100vh",
    width: "100vw",
    transformOrigin: "top",
    transition: "transform .5s ease",
    transform: "scaleY(0)",
  },
  enterActive: {
    transform: "scaleY(1)",
  },
  enterDone: {
    transform: "scaleY(1)",
  },
  exitActive: {
    transform: "scaleY(0)",
  },
})

export default function SliderComponent({ children, pages }) {
  const classes = useStyles()
  let [page, setPage] = useState(null);

  const show = page => {
    const SpecificPage = pages[page];
    if (!page) {
      return null
    }
    return <SpecificPage setPage={setPage} />;
  } 

  return (
    <CSSTransition in={children !== null} timeout={300} classNames={{
        enterActive: classes.enterActive,
        enterDone: classes.enterDone,
        exitActive: classes.exitActive,
      }}>
        <div className={classes.slideUp}>
          <div className={classes.exit} onClick={() => setPage(null)}>+</div>
          {show(page)}
        </div>
    </CSSTransition>
  )
}
