import React, { useState } from "react";
import {createUseStyles} from 'react-jss'
import ListItem from "../components/ui/list/listItem";
import { homeData } from "../data/home";
import AboutMe from "../components/aboutMe";
import { CSSTransition } from 'react-transition-group';
import SliderComponent from "../components/sliderComponent";

const tabletBreak = '@media (max-width: 1250px)';
const mobileBreak = '@media (max-width: 720px)';

const useStyles = createUseStyles({
  container: {
    display: 'grid',
    gridTemplateColumns: "1fr 4fr",
    height: '100%',
    backgroundColor: "#171717",
    padding: "150px",
    paddingBottom: "0px",
    paddingRight: "0px",
    boxSizing: "border-box",
    overflow: "auto",
    [tabletBreak]: {
      gridTemplateColumns: "1fr",
      padding: "20px",
      paddingRight: "0px",
    },
    [mobileBreak]: {
      padding: "20px",
      paddingRight: "0px",
    }
  },
  left: {
    fontSize: "14px",
    letterSpacing: "normal",
  },
  list: {
    display: "flex",
    flexFlow: "column nowrap",
    [mobileBreak]: {
      marginTop: "30px",
    },
    "& li:not(:first-child)": {
      marginTop: "60px"
    }
  },
  span: {
    color: "grey",
    "&:hover": {
      color: "#fff"
    }
  },
  slideUp: {
    display: "flex",
    position: "absolute",
    top: "0",
    left: "0",
    backgroundColor: "#000",
    width: "100vw",
    height: "100vh",
  },
  enter: {
    maxHeight: '0px',
  },
  enterActive: {
    overflow: 'auto'
  },
  enterDone: {
    maxHeight: '2100vh',
    transition: 'max-height 1.5s cubic-bezier(0.77,0,0.175,1)',
  },
  exitActive: {
    maxHeight: '2100vh',
  },
  exitDone: {
    overflow: 'hidden',
    maxHeight: '0px',
    transition: 'max-height 1.5s cubic-bezier(0.77,0,0.175,1)'
  },
})

const components = {
  aboutMe: AboutMe
};

const Home = () => {
  const classes = useStyles();
  let [page, setPage] = useState(null);

  const show = page => {
    const SpecificPage = components[page];
    if (!page) {
      return <div style={{display:"block", width:"100%", marginTop:"30px"}}></div>
    }
    return <SpecificPage setPage={setPage} />;
  } 

  return (
    <main className={classes.container}>
      <div className={classes.left}><span className={classes.span}>Greg Miller</span></div>
      <ol className={classes.list}>
        {homeData.map((o, index) => <ListItem key={index} {...o} onClick={() => setPage(o.page)} />)}
      </ol>
      <CSSTransition in={page !== null} timeout={200} classNames={{
        enter: classes.enter,
        enterActive: classes.enterActive,
        enterDone: classes.enterDone,
        exitActive: classes.exitActive,
        exitDone: classes.exitDone,
      }}>
        <div className={classes.slideUp}>
          {show(page)}
        </div>
      </CSSTransition>
    </main>
  )
}

export default Home;
